package chapter01;

public class InvertedTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int a = 0 ; a < 6 ; a++)
		{
			for (int b = 6 ; b > a ; b--)
			{
				System.out.print("X");
			}
			System.out.println("\n");
		}

	}

}
