package chapter01;

public class Triangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int counta3 = 1;
		for (int counta1 = 1 ; counta1 <= 6 ; counta1++)
		{
			for (int count2 = 1 ; count2 <= counta3 ; count2++)
			{
				System.out.print("X");
			}
			counta3++;
			System.out.println("\n");
		}
		
	}

}
